<?phpnamespace App;use Illuminate\Database\Eloquent\Model;use App\Traits\ActivityLog\LogsActivity;use Illuminate\Database\Eloquent\SoftDeletes;use Spatie\Activitylog\Contracts\Activity;class Document extends Model{    use LogsActivity, SoftDeletes;    /**     * The table associated with the model.     *     * @var string     */    protected $table = 'user_documents';    protected $fillable = [        'document_name',        'document',        'user_id',        'status'    ];    protected static $logName = 'User Documents';    protected static $ignoreChangedAttributes = ['created_at','updated_at'];    protected static $logAttributes = ["status"];    protected static $logOnlyDirty = true;    protected static $recordEvents = ['updated','deleted'];    public function user()    {        return $this->belongsTo('App\User','user_id')->withDefault();    }    public function logDetails()    {        return $this->user->first_name . " " . $this->user->last_name . " " . $this->user->account_number . " - " . $this->document;    }    public function tapActivity(Activity $activity, string $eventName)    {        if($eventName == "deleted"){            $activity->properties = [];        }    }}