<?phpnamespace App\Http\Controllers;use Illuminate\Http\Request;use Illuminate\Support\Facades\Log;use App\Services\Permission\PermissionFacade;use App\Services\Role\RoleFacade;class PermissionController extends Controller{    public function __construct(PermissionFacade $permission, RoleFacade $role)    {                $this->permission = $permission;        $this->role = $role;    }    public function index(Request $request)    {      $permissions = $this->permission::getAll($request);      $roles = $this->role::getAll($request);      return view('backend.administration.roles_permissions.permission_list', compact('permissions', 'roles'));    }    public function create()    {      return view('backend.administration.roles_permissions.modals.create_permission');    }    public function store(Request $request)    {      $permission = $this->permission::create($request);       return response()->json([        'result'=>'success',        'action'=>'store',        'choose' => 'permission',        'message'=>_lang('Saved Successfully'),        'data'=>$permission      ]);    }    public function destroy($id)    {      $this->permission::delete($id);      return redirect('admin/administration/roles_permissions')->with('success',_lang('Deleted Successfully'));    }   }