<?phpnamespace App\Http\Controllers;use Illuminate\Http\Request;use Illuminate\Support\Facades\Log;use App\Services\Permission\PermissionFacade;use App\Services\Role\RoleFacade;class RoleController extends Controller{    public function __construct(      PermissionFacade $permission,       RoleFacade $role)    {                $this->permission = $permission;        $this->role = $role;    }    public function create(Request $request)    {      $permission = $this->permission::getAll($request);      return view('backend.administration.roles_permissions.modals.create_role', compact('permission'));    }    public function store(Request $request)    {      $role = $this->role::create($request);       return response()->json([        'result'=>'success',        'action'=>'store',        'choose' => 'roles',        'message'=>_lang('Saved Successfully'),        'data'=>$role]      );    }    public function edit(Request $request, $id)    {      $permission = $this->permission::getAll($request);      $role = $this->role::get($id);      return view('backend.administration.roles_permissions.modals.edit_role', compact('permission', 'role', 'id'));    }    public function update(Request $request, $id)    {      $role = $this->role::update($request, $id);      return response()->json([        'result'=>'success',        'action'=>'update',        'message'=>_lang('Updated Successfully'),        'data'=>$role      ]);    }    public function destroy($id)    {      $this->role::delete($id);      return redirect('admin/administration/roles_permissions')->with('success',_lang('Deleted Successfully'));    }   }